public void setValue(Planet inf, int receivedIter) {
    if (this.iter == receivedIter) {
        this.currentForce.add(info, inf);
        this.nbReceived ++ ;
        if (this.nbReceived == this.nbvalues)
            moveBody();
        }
        else {
            this.prematureValues.add(new Carrier (inf, receivedIter));
        }
    }

