importPackage(java.io);
var ID   = 1 + "_" + $REP;
var RUNS = 3;
var f = new File(java.lang.System.getProperty("java.io.tmpdir"), "fc4_1" + ID);
var it = 0;
if (f.exists()) {
var input = new BufferedReader(new FileReader(f));
it = java.lang.Integer.parseInt(input.readLine());
input.close();
f["delete"]();
}
it++;
if (it < RUNS) {
loop = true;
f.createNewFile();
var output = new BufferedWriter(new FileWriter(f));
output.write("" + it);
output.close();
} else {
loop = false;
}
